import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

const Header = (props) => {
  return (
    <header>
      <h1 className="title">Simple Grocery List</h1>
      <span className="item-total">Items: </span>
    </header>
  )
}
const Item = (props) => {
  return(
    <div className="item">
      <span className="item-name">
        {props.name}
      </span>
      <Counter />
    </div>
  )
}
class Counter extends Component{
  state = {
    quantity : 0
  };
  moreItem= () =>{
    this.setState({
      score: prevState.quantity 
    })
  }
}
class App extends Component {
  render() {
    return (
      <div className="container">
        <Header />
      </div>
    );
  }
}

export default App;
