import React, { Component } from 'react';
import './App.css';
// header info
const Header = (props) => {
  return (
    <header>
      <h1 className="title">Simple Grocery List</h1>

      <span className="item-total">Items: { props.items.length }</span>
    </header>
  )
}
// function  to create list items 
const Item = (props) => {
  return(
    <div className="item">
      <span className="item-name">
       Chicken
      </span>
      <Counter />
    </div>
  )
}
// class for adding and subtracting the quantity
class Counter extends Component{
  state = {
    quantity : 0
  };
  moreItem= () =>{
    this.setState( prevState => ({
      quantity: prevState.quantity + 1
    }));
  }
  lessItem= () => {
    this.setState ( prevState => ({
      quantity: prevState.quantity - 1
    }));
  }
  render(){
    return(
      <div className="counter">
        <button className="counter-action less" onClick={this.lessItem}>-</button>
        <span className="quantity">{this.state.quantity}</span>
        <button className="counter-action more" onClick={this.moreItem}>+</button>
      </div>
    )
  }
}
// home app for building
class App extends Component {
  render() {
    return (
      <div className="container">
        <Header  />
        <Item />
      </div>
    );
  }
}

export default App;
