import React, { Component } from "react"

class Form extends Component {
  constructor(props){
    super(props);

    this.initialState= {
      zip: "zip code"
    };
    this.state = this.initialState;
  }
  handleChange = event => {
    const {zip, value} = event.target;

    this.setState({
      [zip] : value
    });
  }
  render() {
    const {zip} = this.state;

    return(
      <form>
        <label> Enter your zip code:</label>
        <input 
          type="text"
          name= "zip"
          value={zip}
          onChange={this.handleChange} />
      </form>
    );
  }
}

export default Form
